(166, 1, '2020-07-30 17:40:17', '2020-07-30 10:40:17', '<!-- wp:image {\"align\":\"center\",\"id\":158,\"sizeSlug\":\"large\"} -->\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-large\"><img src=\"http://localhost/sidesa/wp-content/uploads/2020/07/pinoraya_kec.jpg\" alt=\"\" class=\"wp-image-158\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p class=\"has-text-align-center\">Peta Wilayah Pino</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. Kondisi Geografis</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Desa Tanggo Raso adalah wilayah yang memiliki luas wilayah 147 Ha.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Dengan Jumlah Penduduk :</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<ul><li>Laki-laki&nbsp&nbsp&nbsp&nbsp: 763 orang</li><li>Perempuan : 689 orang</li><li>Total&nbsp &nbsp &nbsp &nbsp &nbsp &nbsp: 1.452 orang</li></ul>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>2. Batas-batas</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Batas Sebelah Utara : Desa Talang Padang</li><li>Batas Sebelah Timur : Desa Padang Serasan</li><li>Batas Sebelah Selatan : Desa Padang Serasan</li><li>Batas Sebelah Barat : Desa Pasar Pino</li></ul>\n<!-- /wp:list -->', 'Profil Wilayah Desa', '', 'inherit', 'closed', 'closed', '', '101-revision-v1', '', '', '2020-07-30 17:40:17', '2020-07-30 10:40:17', '', 101, 'http://localhost/sidesa/2020/07/30/101-revision-v1/', 0, 'revision', '', 0),
